    'Media móvil N=10', ...
    'Media móvil N=20', ...
    'Filtro pasabajos α=0.1', ...
    'Filtro pasabajos α=0.05'
};

colores_filtros = [
    0.2 0.2 1;   % Azul
    1.0 0.6 0;   % Naranja
    0.0 0.7 0;   % Verde
    0.5 0.2 0.9; % Violeta
    0.0 0.5 0.5  % Cian oscuro
];

nombres_base = nombres_filtros;

% === Crear figura con checkbox interactivo ===
f = figure('Name', 'Comparación de filtros con checkboxes');
ax = axes('Parent', f);
hold(ax, 'on');

lineas = gobjects(length(filtros), 1);
for i = 1:length(filtros)
    lineas(i) = plot(ax, t, filtros{i}, 'LineWidth', 1.5, 'Color', colores_filtros(i,:));
end
xlabel('Tiempo [s]');
ylabel('Ángulo [grados]');
title('Comparación entre filtros aplicados');
legend(nombres_filtros, 'Location', 'best');
xlim([0 duracion_total]);
grid on;

% === Crear checkboxes ===
for i = 1:length(filtros)
    uicontrol('Style', 'checkbox', ...
        'String', nombres_filtros{i}, ...
        'Value', 1, ...
        'Position', [20, 20 + 30*(length(filtros)-i), 200, 20], ...
        'Callback', @(src,~) set(lineas(i), 'Visible', logicalToOnOff(get(src, 'Value'))));
end

% === Extraer intervalo entre 1 y 2 s ===
idx_intervalo = t >= 1 & t <= 2;
t_intervalo = t(idx_intervalo);

% === Graficar solo intervalo [1, 2] con leyenda extendida ===
figure;
hold on;

leyendas_completas = cell(1, length(filtros));

for i = 1:length(filtros)
    datos_intervalo = filtros{i}(idx_intervalo);

    % Calcular media y error cuadrático medio
    media_valor = mean(datos_intervalo);
    error_cuadratico = sqrt(mean((datos_intervalo - media_valor).^2));  % RMS
    error_porcentaje = (error_cuadratico / abs(media_valor)) * 100;     % [%]

    % Graficar
    plot(t_intervalo, datos_intervalo, 'LineWidth', 1.5, 'Color', colores_filtros(i,:));

    % Leyenda con ± error
    leyendas_completas{i} = sprintf('%s (μ=%.2f ± %.2f%%)', ...
        nombres_base{i}, media_valor, error_porcentaje);
end

xlabel('Tiempo [s]');
ylabel('Ángulo [grados]');
title('Comparación de filtros entre 1 y 2 segundos');
legend(leyendas_completas, 'Location', 'best');
xlim([1 2]);
grid on;

% === Función auxiliar para convertir lógico a 'on'/'off' ===
function estado = logicalToOnOff(valor)
    if valor
        estado = 'on';
    else
        estado = 'off';
    end
end